function recomendar(genero) {
let edad =  document.getElementById("edad").value;
let recomendaciones = document.getElementById("recomendacion");
let img = document.createElement("img");
    switch(genero){
        case 'drama':
            if (edad < 13){
                recomendaciones.textContent = "Casabanca  ";
                
                img.src = "https://c8.alamy.com/compes/db57m7/casablanca-warner-bros-1942-dirigida-por-michael-curtiz-poster-de-pelicula-db57m7.jpg" ;
                img.style.width = "100px";
                recomendaciones.appendChild(img);

            } else{
                if (edad < 16){
                    recomendaciones.textContent = "The shawsshan Redemption  ";
                    img.src = "data:image/jpeg;base64," ;
                    img.style.width = "100px";
                    recomendaciones.appendChild(img);
                     
                }else{
                    recomendaciones.textContent = "Taxi Driver  ";
                    img.src = "data:image/jpeg;base64," ;
                    img.style.width = "100px";
                    recomendaciones.appendChild(img);
                }
            }
            break;
               case 'comedia':
                 if (edad < 13){
                    recomendaciones.textContent = "Back to the future  ";
                    img.src = "data:image/jpeg;base64," ;
                    img.style.width = "100px";
                    recomendaciones.appendChild(img);
                     
                }else{
                    if (edad < 16){
                        recomendaciones.textContent = "The Truman Show  ";
                        img.src = "data:image/jpeg;base64," ;
                        img.style.width = "100px";
                        recomendaciones.appendChild(img);
                     
                    }else{
                        recomendaciones.textContent = "The  Wolf of wall Street  ";
                        img.src = "data:image/jpeg;base64," ;
                        img.style.width = "100px";
                        recomendaciones.appendChild(img);
                    }

                }
                break;
                case 'musical':
                if ( edad < 13){ 
                    recomendaciones.textContent = "La La Land  ";    
                    img.src = "data:image/jpeg;base64," ;
                    img.style.width = "100px";
                    recomendaciones.appendChild(img);

                }else{
                    if (edad < 16){
                        recomendaciones.textContent = "Los misirables  ";
                        img.src = "data:image/jpeg;base64," ;
                        img.style.width = "100px";
                        recomendaciones.appendChild(img);
                     
                    }else{
                        recomendaciones.textContent = "The Rocky Horror picture Show  ";
                        img.src = "data:image/jpeg;base64," ;
                        img.style.width = "100px";
                        recomendaciones.appendChild(img);
                     
                    }
                }
                break;
                case 'crimen':
                if ( edad < 13){
                    recomendaciones.textContent = "No ay opciones para esta edad";    

                }else{
                    if (edad < 16){
                        recomendaciones.textContent = "El secreto de tus ojos  ";
                        img.src = "data:image/jpeg;base64," ;
                        img.style.width = "100px";
                        recomendaciones.appendChild(img);
                     
                    }else{
                        recomendaciones.textContent = "The godfather  ";
                        img.src = "data:image/jpeg;base64," ;
                        img.style.width = "100px";
                        recomendaciones.appendChild(img);
                     
                    }
                }
                break;
                
    }

}